CREATE DATABASE `bootcamp_data` /*!40100 DEFAULT CHARACTER SET latin1 */;

CREATE TABLE `bootcamp_data`.`movies` (
  `id` bigint(11) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL,
  `budget` bigint(11) DEFAULT NULL,
  `revenue` bigint(11) DEFAULT NULL,
  `imdb` varchar(255) NOT NULL,
  `language` varchar(50) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=47087 DEFAULT CHARSET=latin1;


